import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

function Page(props) {

  var page = {
    blocks: [],
    dimensions: {
      width:props.data.size.width,
      height:props.data.size.height,
      aspectRatio: props.data.size.height/props.data.size.width
    },

  };

  for (var i = 0; i < props.data.blocks.length; i++) {
    page.blocks.push({
        paragraphs:[],
        coordinates: {
          left_top: {
            x: props.data.blocks[i].left_top_v.x,
            y: props.data.blocks[i].left_top_v.y
          },
          right_bot: {
            x: props.data.blocks[i].right_bot_v.x,
            y: props.data.blocks[i].right_bot_v.y
          }
        }
    });

    for (var j = 0; j < props.data.blocks[i].paragraphs.length; j++) {
      page.blocks[i].paragraphs.push({
        element:props.data.blocks[i].paragraphs[j].para_str,
        coordinates: {
          left_top: {
            x: props.data.blocks[i].paragraphs[j].left_top_v.x,
            y: props.data.blocks[i].paragraphs[j].left_top_v.y
          },
          right_bot: {
            x: props.data.blocks[i].paragraphs[j].right_bot_v.x,
            y: props.data.blocks[i].paragraphs[j].right_bot_v.y
          }
        }
      });

    }

  }

  return (
    console.log(window.innerWidth),
    console.log(window.innerHeight),
    <body style={{
      paddingTop: '5%',
      paddingBottom: '5%',
      minWidth: '2200px',
      minHeight: '950px'
    }}>
        <div class='page' style={{
          position: 'relative',
          paddingBottom: ((1-page.dimensions.aspectRatio))*100 + 3 + 'vw',
          paddingTop: '3%',
          width: '70%',
          margin: 'auto'
        }}>
        {
          page.blocks.map(block => (
            console.log((block.coordinates.right_bot.x - block.coordinates.left_top.x) * 100),
            <div class='block' style={{
              position:'relative',
              left:(block.coordinates.left_top.x * 100)+'%',
              top:(block.coordinates.left_top.y * 100)+'%',
              height:(block.coordinates.right_bot.y - block.coordinates.left_top.y) * 100 + 'vw',
              width: (block.coordinates.right_bot.x - block.coordinates.left_top.x) * 100 + '%'
            }}>
              {
                block.paragraphs.map(paragraph => (
                  <p style={{
                    position:'relative',
                    fontSize: '1.5vw',
                    left:(paragraph.coordinates.left_top.x * 100)+'%',
                    right:(paragraph.coordinates.right_bot.x * 100)+'%',
                    top:(paragraph.coordinates.left_top.y * 100)+'%',
                    bottom:(paragraph.coordinates.right_bot.y * 100)+'%'
                  }}>
                  {paragraph.element}
                  </p>
                ))
              }
            </div>

          ))
        }
        </div>
      </body>
  );

}

function loadData (path) {
  try {
    var data = require('/Users/jaminapps/Documents/Programming/react/json-to-webpage/src/out.json');
    console.log(data);
    return require('/Users/jaminapps/Documents/Programming/react/testparse/src/out.json');
  }
  catch(err) {
    console.log('error');
  }
}

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items:{},
      isLoaded:false
    }
  }

  componentDidMount() {

    try {
      var data = require('/Users/jaminapps/Documents/Programming/react/testparse/src/out.json');
      this.setState({
        item: data,
        isLoaded:true,
      });
    }
    catch(err) {
      console.log('error');
    }

  }

  render() {

    var { isLoaded, item } = this.state;
    console.log(item);

    if (!isLoaded) {
      return (
        <div>Loading...</div>

      )
    }
    else {
          return (
            <Page data={item} />
          );
    }

  }


}

export default App;
