      });
      var concText= props.data.blocks[i].paragraphs[j].para_str + '\n';
      page.blocks[i].elements += concText;
      console.log(page.blocks[i].elements);
    }
  }

  return (

      <div class='page' style={{
        position: 'absolute',
        paddingBottom: (page.dimensions.aspectRatio)*100 + 'vw',
        paddingTop: '3%',
        width: '70%',
        left:'15%',
        right:'15%',
        fontSize:'10vw'
