import logo from './logo.svg';
import './App.css';

export default App;

function Image(props) {
  var images = [];
  var aspectRatio = props.pageSize.height/props.pageSize.width;
  var docImgRatio = [];

  props.images.map(image => (
    images.push({
      path: ('/Users/jaminapps/Documents/Programming/react/testparse/src/images/img' + image.index) + '.jpg',
      coordinates: {
        left_top: {
          y:image.left_top_v.y,
          x:image.left_top_v.x
        },
        right_bot: {
          y:image.right_bot_v.y,
          x:iamge.right_bot_v.x
        }
      },
      imageRatio: (image.right_bot_v.x - image.left_top_v.x)/props.pageSize.width
    })
  ))

  return (
    images.map(image => (
        <img src={image.path} alt="image not loaded" style={{
          position:relative,
          left:image.coordinates.left_top.x,
          top:image.coordinates.left_top.y,
          paddingBottom: aspectRatio*100+'%',
          width: iamge.imageRatio*100+'%'
        }} />
    ))
  );
}

function Page(props) {
  var page = {
    blocks: [],
