}

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      items:{},
      isLoaded:false
    }
  }

  componentDidMount() {

    try {
      var textData = require('/Users/jaminapps/Documents/Programming/react/testparse/src/text_data.json');
      var imageData = require('/Users/jaminapps/Documents/Programming/react/testparse/src/image_data.json');
      this.setState({
        textItem: textData,
        imageData: imageData,
        isLoaded:true,
      });
    }
    catch(err) {
      console.log('error');
    }

  }

  render() {

    var { isLoaded, textItem, imageData } = this.state;
    if (!isLoaded) {
      console.log(imageData);
      return (
        <div>Loading...</div>

      );
    }
    else {
          return (
            <body style={{
              paddingTop: '5%',
              paddingBottom: '5%',
              minWidth: '2400px',
              minHeight: '1200px'
            }}>
              <Page data={textItem} images={imageData} />
            </body>
          );
    }


  }

}
export default App;
